                            <TableCell>{val.ReleaseDate}
                            <p><input className="up" type="date" /></p>
                            <button className='up1'>update</button>
                            </TableCell>
                            <TableCell>{val.Budget}
                            <p><input className="up" type="number" /></p>
                            <button className='up1'>update</button>
                            </TableCell>
                            <TableCell>{val.Collection}
                            <p><input className="up" type="number" /></p>
                            <button className='up1'>update</button>
                            </TableCell>
                            <TableCell>

                                <Button variant="contained" style={{backgroundColor:'#f44336',margin:'2px',width:'60px'}} >Delete</Button>
                            </TableCell>
                            </TableRow>
