import { Button, ButtonBase, Table, TableBody, TableCell, TableHead, TableRow } from '@material-ui/core'
import React,{ useEffect, useState } from 'react';
import './App.css';
import Axios from 'axios'

function App() {
  const [moviename,setMovieName] = useState('')
  const [language,setLanguage] = useState('')
  const [release,setRelease] = useState('not available')
  const [budget,setBudget] = useState(0)
  const [collection,setCollection] = useState(0)

  const[movieList,setMovieList] = useState([])

  useEffect(() => {
    Axios.get("http://localhost:3001/read").then((response)=>{
      setMovieList(response.data)
    })
  }, [])

  const addToList = ()=>{
    console.log(moviename+language);
    Axios.post("http://localhost:3001/insert",{ movieName:moviename,
    Language:language,
    ReleaseDate:release,
    Budget:budget,
    Collection:collection})
  }

  return (
    <div className="App">
     <h1>Crud in Mern</h1>
     <label>Movie Name</label>
      <input type="text" onChange={(event)=>{setMovieName(event.target.value)}}/>
      <label>Language</label>
      <input type="text"  onChange={(event)=>{setLanguage(event.target.value)}}/>
      <label>Release Date</label>
      <input type="date"  onChange={(event)=>{setRelease(event.target.value)}}/>
      <label>Budget</label>
      <input type="number"  onChange={(event)=>{setBudget(event.target.value)}}/>
      <label>Collection</label>
      <input type="number"  onChange={(event)=>{setCollection(event.target.value)}}/>
      <button onClick={addToList}>ADD</button>

      <h1>Movie List</h1>
      <Table style={{marginTop:'30px'}}>
            <TableHead style={{ boxShadow:'-1px 4px 20px -6px rgba(0,0,0,0.75)',backgroundColor:'black'}}>
                <TableRow>

                    <TableCell style={{color:'white'}}>MovieName</TableCell>
                    <TableCell style={{color:'white'}}>Language</TableCell>
                    <TableCell style={{color:'white'}}>Release Date</TableCell>
                    <TableCell style={{color:'white'}}>Budget</TableCell>
                    <TableCell  style={{color:'white'}}>Collections</TableCell>
                    <TableCell></TableCell>
                    </TableRow>
            </TableHead>
            <TableBody>
                {
                    movieList.map((val,key)=>(
                        <TableRow>

                            <TableCell>{val.movieName}</TableCell>
                            <TableCell>{val.Language}</TableCell>
                            <TableCell>{val.ReleaseDate}</TableCell>
                            <TableCell>{val.Budget}</TableCell>
                            <TableCell>{val.Collection}</TableCell>
                            <TableCell>
                                <Button variant="contained" style={{backgroundColor:'#26a69a',margin:'2px',width:'60px'}}>Edit</Button>
                                <Button variant="contained" style={{backgroundColor:'#f44336',margin:'2px',width:'60px'}} >Delete</Button>
                            </TableCell>
                            </TableRow>
                    ))
                }
            </TableBody>
        </Table>
    </div>
  );
}

export default App;
